package org.example

class App {
    fun calculaSalario(): Double{
        println("Digite o salario: ")
        val salario = readLine()?.toDoubleOrNull() ?: throw IllegalArgumentException("Salario invalido")
        
        println("Digite o beneficio: ")
        val beneficio = readLine()?.toDoubleOrNull() ?: throw IllegalArgumentException("BenefÃ­cio invalido")

        var aliquota: Double = when {
            salario > 0 && salario <= 1100 -> 0.05
            salario > 1100 && salario <= 2500 -> 0.1
            else -> 0.15
        }

        val imposto = salario * aliquota;
        val salarioLiquido = (salario - imposto) + beneficio;
        return salarioLiquido
    }
}

fun main() {
    val app = App()
    println(app.calculaSalario())
}
